import React, { useState, useContext } from 'react';
import { Modal, Button } from 'react-bootstrap';
import './Home.css';
import { CartContext } from '../context/CartContext';
import AdCarousel from './AdCarousel';

const products = [
  { id: 1, name: "", price:  11495.00, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dwcff07af1/images/Fastrack/Catalog/3297KM02_1.jpg?sw=360&sh=360", discount: 10 },
  { id: 2, name: "", price: 11995, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw078d5d90/images/Fastrack/Catalog/3297KM01_1.jpg?sw=360&sh=360", discount: 5 },
  { id: 3, name: "", price: 18995, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw395381cb/images/Helios/Catalog/KCWGL2217202MN_1.jpg?sw=360&sh=360", discount: 15 },
  { id: 4, name: "", price: 35000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw5bb4610e/images/Titan/Catalog/1893KL01_1.jpg?sw=360&sh=360", discount: 20 },
  { id: 5, name: "", price: 10000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw99f69b21/images/Fastrack/Catalog/3309NL01_1.jpg?sw=360&sh=360", discount: 12 },
  { id: 6, name: "", price: 23000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw146b1bfa/images/Titan/Catalog/90110NM01_1.jpg?sw=360&sh=360", discount: 8 },
  { id: 7, name: "", price: 18000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw7d7b0b43/images/Titan/Catalog/90126SL01_1.jpg?sw=360&sh=360", discount: 5 },
  { id: 8, name: "", price: 14000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw5a2a937b/images/Titan/Catalog/90110WL03_1.jpg?sw=360&sh=360", discount: 10 },
  { id: 9, name: "", price: 15000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dw54208592/images/Titan/Catalog/95291WM01_1.jpg?sw=360&sh=360", discount: 5 },
  { id: 10, name: "", price: 22000, image: "https://www.titan.co.in/dw/image/v2/BKDD_PRD/on/demandware.static/-/Sites-titan-master-catalog/default/dwbdd2cef8/images/Fastrack/Catalog/38077AP03_1.jpg?sw=360&sh=360", discount: 15 },
];

const offers = [
  { id: 1, description: "Flat 10% off on womwn's watch's", image: "https://image.titancomm.co.in/lib/fe3111747364047d711d72/m/1/1fbde736-1cc6-4b53-bd84-7ab19c84fe97.png" },
  { id: 2, description: "40% off on men's watch's", image: "data:image/jpeg;base64," },
  { id: 3, description: "20% off on kids watchs", image: "data:image/jpeg;base64," },
  { id: 4, description: "Flat 15% off on Gift watchs", image: "data:image/jpeg;base64," },
  { id: 5, description: "Buy one get one free", image: "data:image/jpeg;base64," },
  

];

const Home = () => {
  const { addToCart } = useContext(CartContext);
  const [quantities, setQuantities] = useState(Array(products.length).fill(1));
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleAddToCart = (product, index) => {
    alert ('Item Added to cart');
    const quantity = quantities[index];
    addToCart({ ...product, quantity });
    setQuantities((prevQuantities) => {
      const newQuantities = [...prevQuantities];
      newQuantities[index] = 1; // Reset quantity after adding to cart
      return newQuantities;
    });
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  }; 

  const handleCloseModal = () => setShowModal(false);

  return (
    <div className="home">
      <h1>Welcome to Titan World!</h1>
      <AdCarousel />
      <h2 className="section-title">Exclusive Offers</h2>
      <div className="offers-container">
        {offers.map((offer) => (
          <div className="offer-card" key={offer.id}>
            <img src={offer.image} alt={offer.description} className="offer-image" />
            <div className="offer-description">
              <p>{offer.description}</p>
            </div>
          </div>
        ))}
      </div>
      <h2 className="section-title">Featured Products</h2>
      <div className="products-container">
        {products.map((product, index) => {
          const discountedPrice = product.price - (product.price * product.discount / 100);
          return (
            <div className="product-card" key={product.id} onClick={() => handleProductClick(product)}>
              <img src={product.image} alt={product.name} className="product-image" />
              <div className="product-details">
                <h5 className="product-name">{product.name}</h5>
                <p className="product-price">
                  <span className="discounted-price">₹{new Intl.NumberFormat('en-IN').format(discountedPrice)}</span>
                  <span className="original-price"> ₹{new Intl.NumberFormat('en-IN').format(product.price)}</span>
                  <span className="discount-badge">-{product.discount}%</span>
                </p>
                <div className="quantity-control">
                  <button onClick={(e) => { e.stopPropagation(); setQuantities((prev) => { const newQty = [...prev]; newQty[index] = Math.max(1, newQty[index] - 1); return newQty; }); }}>-</button>
                  <span>{quantities[index]}</span>
                  <button onClick={(e) => { e.stopPropagation(); setQuantities((prev) => { const newQty = [...prev]; newQty[index] += 1; return newQty; }); }}>+</button>
                </div>
                <button 
                  className="btn btn-primary add-to-cart-btn" 
                  onClick={(e) => { e.stopPropagation(); handleAddToCart(product, index); }}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {selectedProduct && (
        <Modal show={showModal} onHide={handleCloseModal} centered>
          <Modal.Header closeButton>
            <Modal.Title>{selectedProduct.name}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img src={selectedProduct.image} alt={selectedProduct.name} className="img-fluid" />
            <p>Price: ₹{new Intl.NumberFormat('en-IN').format(selectedProduct.price)}</p>
            <p>Product Description: A great product for your needs!</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleCloseModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </div>
  );
};

export default Home;
